#! /bin/bash

dbhost=
dbname=
dbuser=
dbpass=

function check(){

goods=$(mysql -h$dbhost -u$dbuser -p$dbpass -e "use $dbname;SELECT img_id, goods_id, img_url, img_original FROM goods_gallery WHERE goods_id in ($1) ORDER BY img_id LIMIT 500 ")
echo "$goods" | while read row; do
goods_id=$(echo "$row"|awk -F"\t" '{print $2}')
echo $goods_id|grep -E '^[0-9]+$' > /dev/null
if [ $? -eq 0 ]; then
    img_id=$(echo "$row"|awk -F"\t" '{print $1}')
    img_url=$(echo "$row"|awk -F"\t" '{print $3}')
    img_original=$(echo "$row"|awk -F"\t" '{print $4}')
    size=$(identify $img_original | awk '{print $3}' | awk -F'x' '{print $1}');
    if [ $size -ge 1500 ]; then
        echo $img_id
    fi
fi
done

}

